const accordions=document.querySelectorAll(".accordion"),openAccordion=c=>{const o=c.querySelector(".accordion__content");c.classList.add("accordion__active"),o.style.maxHeight=o.scrollHeight+"px"},closeAccordion=c=>{const o=c.querySelector(".accordion__content");c.classList.remove("accordion__active"),o.style.maxHeight=null};accordions.forEach(c=>{const o=c.querySelector(".accordion__intro"),e=c.querySelector(".accordion__content");o.onclick=()=>{e.style.maxHeight?closeAccordion(c):(accordions.forEach(c=>closeAccordion(c)),openAccordion(c))}});